import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import email_icon from '../Assets/email.png';

const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  setMessage('');

  try {
    const response = await fetch('http://localhost:5000/check-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email }),
    });

    if (!response.ok) {
      throw new Error('Failed to check email. Please try again.');
    }

    const result = await response.json();

    if (result.exists) {
      emailjs.sendForm('service_jy6oq6w', 'template_m5k5p9e', e.target, 'adityaadangwal@gmail.com')
        .then((result) => {
          setMessage('Password reset email sent!');
        }, (error) => {
          console.log(error.text);
          setMessage('Failed to send password reset email.');
        });
    } else {
      setMessage("Email doesn't exist");
    }
  } catch (error) {
    console.error('Error:', error);
    setMessage('An error occurred while checking the email. Please check your network connection.');
  } finally {
    setLoading(false);
    setEmail('');
  }


  return (
    <div className='container'>
      <div className="header">
        <div className="text">Password Reset</div>
        <div className="underline"></div>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="inputs">
          <div className="input">
            <img src={email_icon} alt="Email icon" />
            <input 
              type="email" 
              placeholder='Enter Your Email' 
              name="email" 
              value={email} 
              onChange={handleInput} 
              required
            />
          </div>
          <div className="submit-container">
            <div className="submit">
              <button type='submit' disabled={loading}>
                {loading ? 'Submitting...' : 'Submit'}
              </button>
            </div>
          </div>
        </div>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ForgotP;
