import React, { useState } from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';

const Dashboard = () => {
    let count = 0;

    const queryParams = new URLSearchParams(window.location.search);
    const Username = queryParams.get('Username');
    
    const [showImage, setShowImage] = useState(false);
    const [bold, setBold] = useState({
        fontWeight: 'normal'
    });
    const [ImageStyle, setImageStyle] = useState({
        height: '25px',
        width: '25px',
        filter: 'invert(1)'
    });
    const [hambfunc, sethambfunc] = useState(false);

    const handleShowO = () => {
        setShowImage(true);
        setBold({ fontWeight: 'bold' });
    };

    const handleShowL = () => {
        setShowImage(false);
        setBold({ fontWeight: 'normal' });
    };

    const imagehandleO = () => {
        setImageStyle({
            margin: '-2px',
            height: '28px',
            width: '28px',
            filter: 'invert(1)'
        });
    };

    const imagehandleL = () => {
        setImageStyle({
            height: '25px',
            width: '25px',
            filter: 'invert(1)'
        });
    };

    const ham = () => {
       sethambfunc((prevState) => !prevState);
    };

    return (
        <div className='main'>
            <div className="left">
                <div className
                ="lists account " onMouseEnter={handleShowO} onMouseLeave={handleShowL}>
                    <div className="noham">
                    <span style={bold}>@{Username}</span>
                    {showImage && (
                        <div>
                            <img
                                onClick={ham}
                                onMouseEnter={imagehandleO}
                                onMouseLeave={imagehandleL}
                                className='imi'
                                src={dwi}
                                style={ImageStyle}
                                alt="image"
                            />
                        </div>
                    )}
                    </div>
                    {hambfunc && (
                        <div className="ham">                            <div className=" hm1 logout">log out</div>
                            <div className="hm1 updateprofile">update</div>
                        </div>
                    )}
                </div>

                <div className="pt lists dashboard">Dashboard</div>
                <div className="pt lists reports">Reports</div>
                <div className="pt lists settings">Settings</div>

                <div className="footer">
                    <div className="pt letschat">Let's Chat <img src="Assets/le" alt="" /></div>
                    <div className="pt collapse_menu">Collapse Menu</div>
                </div>
            </div>

            <div className="right">Right Content Here</div>
        </div>
    );
};

export default Dashboard;
