import React, { useState, useEffect } from 'react';
import "./UpdateP.css";
import backgroundVideo from '../Assets/background-video.mp4';
import password_icon from '../Assets/password.png';

const UpdateP = () => {
  // Extract the email from query parameters
  const queryParams = new URLSearchParams(window.location.search);
  const Email = queryParams.get('Email'); // Assuming 'Email' is the query parameter name
  
  const [uval, setUval] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Effect to check if the Username is available
  useEffect(() => {
    if (!Username) {
      setMessage('Invalid access. Email parameter is missing.');
    }
  }, [Username]);

  // Handle input change
  const handleInput = (e) => {
    setUval(e.target.value);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage(''); // Clear previous messages

    if (!Username) {
      setMessage('Cannot update password. Username is missing.');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/updatepass', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Username, uval }),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('Password updated successfully');
      } else {
        setMessage(result.msg || 'Error updating password');
      }
    } catch (error) {
      setMessage('There was an error');
    } finally {
      setLoading(false); // End loading state
    }
  };

  return (
    <div className='container'>
      {/* Background Video */}
      <video autoPlay muted loop className="background-video">
        <source src={backgroundVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="content">
        <div className="header">
          <div className="text">Update Password</div>
          <div className="underline"></div>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="inputs">
            <div className="input">
              <img src={password_icon} alt="Password Icon" />
              <input
                type="password"
                placeholder='New Password'
                name="Password"
                onChange={handleInput}
                required
                autoComplete="new-password"
              />
            </div>
            <div className="submit-container">
              <div className="submit">
                <button type='submit' disabled={loading || !Username}>
                  {loading ? 'Updating...' : 'Update'}
                </button>
              </div>
            </div>
          </div>
        </form>
        {message && <p>{message}</p>}
      </div>
    </div>
  );
};

export default UpdateP;
