import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import './Login.css';
import username_icon from '../Assets/username.png';
import password_icon from '../Assets/password.png';
import backgroundVideo from '../Assets/background-video.mp4';

const Login = () => {
    const [user, setUser] = useState({
        Username: "",
        Password: "",
    });
    const [error, setError] = useState(null); // To handle error messages
    const navigate = useNavigate(); // Hook for navigation

    const handleInput = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/login', { // Assuming '/login' is the endpoint
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                credentials: 'include',
                body: JSON.stringify(user),
            });
            const data = await response.json();
            if (data.msg === "great") {
                navigate(`/updatepassword?Email=${user.Username}`); // Redirect to update password page
            } else if (data.msg === "badomen") {
                setError("Wrong Password, try again");
            } else {
                setError("Wrong password or username");
            }
        } catch (error) {
            setError("An error occurred during login");
            console.log("login", error);
        }
    };

    return (
        <div className='container'>
            <video autoPlay muted loop className="background-video">
                <source src={backgroundVideo} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div className='content'>
                <div className="header">
                    <div className="text">Login</div>
                    <div className="underline"></div>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="inputs">
                        <div className="input">
                            <img src={username_icon} alt="Username Icon" />
                            <input
                                type="text"
                                placeholder='Username'
                                name="Username"
                                value={user.Username}
                                onChange={handleInput}
                            />
                        </div>
                        <div className="input">
                            <img src={password_icon} alt="Password Icon" />
                            <input
                                type="password"
                                placeholder='Password'
                                name="Password"
                                value={user.Password}
                                onChange={handleInput}
                            />
                        </div>
                        <div className="forgot-password">
                            Lost Password<span><a href="/password-change">Click here</a></span>
                        </div>
                        <div className="submit-container">
                            <div className="submit"><a href="/register">Sign Up</a></div>
                            <div className="submit"><button type='submit'>Login</button></div>
                        </div>
                    </div>
                    {error && <p>{error}</p>}
                </form>
            </div>
        </div>
    );
}

export default Login;
