import React, { useState } from 'react';
import './Login.css'
import username_icon from '../Assets/username.png'
import password_icon from '../Assets/password.png'

const  Login = ()=>{
    const [user, setUser] = useState({
        Username : "",
        Password : "",
    });
    const handleInput = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value,
        });
    };
    const handleSubmit = async(e) => {
        e.preventDefault();
        console.log(user);
       try{
        const response = await fetch('http://localhost:5000/', {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify(user),

       });
       const data = response.data;
       if(data = "great"){
        window.location.href = `http://localhost:3000/dashboard`;
       }
       
    }catch(error){
        console.log("login", error);
        
    }
    };

    return (
        <body>
            <video src="src\.medQ.mp4"></video>
        <div className='container'>
              <div className="header">
                <div className="text">Login</div>
                <div className="underline"></div>
              </div>
            <form onSubmit={handleSubmit}> 
              <div className="inputs">
                <div className="input">
                    <img src={username_icon} alt="Username Icon" />
                        <input 
                            type="text" 
                            placeholder='Name' 
                            name="Username" 
                            value={user.Username} 
                            onChange={handleInput} 
                        />
                </div>
                <div className="input">
                <img src={password_icon} alt="Password Icon" />
                        <input 
                            type="password" 
                            placeholder='Password' 
                            name="Password" 
                            value={user.Password} 
                            onChange={handleInput} 
                        />
                </div>
                <div className="forgot-password">Lost Password<span> Click here</span></div>
                <div className="submit-container">
                    <div className="submit"><a href="http://localhost:3000/register">Sign Up</a></div>
                    <div className="submit"><button type='submit'>Login</button></div>
                </div>
              </div>
              </form>
        </div>
        </body>
    );
}
export default Login
