// ForgotP.jsx
import React, { useState } from 'react';
import emailjs from 'emailjs-com'; // Make sure emailjs is installed

const ForgotP = () => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Check if the email exists
      const response = await fetch('http://localhost:5000/check-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }), // Send email as JSON
      });

      const result = await response.json();

      if (result.exists) {
        // Send the email using emailjs
        emailjs.sendForm('service_jy6oq6w', 'template_m5k5p9e', e.target, 'user_YOUR_USER_ID')
          .then((result) => {
            console.log(result.text);
            setMessage('Password reset email sent!');
          }, (error) => {
            console.log(error.text);
            setMessage('Failed to send password reset email.');
          });
      } else {
        setMessage("Email doesn't exist");
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred while checking the email.');
    } finally {
      setLoading(false);
      setEmail('');
    }
  };

  const handleInputChange = (e) => {
    setEmail(e.target.value);
  };

  return (
    <div className="forgot-password">
      <h2>Forgot Password</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Sending...' : 'Send Password Reset Email'}
        </button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ForgotP;
