import React, { useState } from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';
import letschat from '../Assets/letschat.png';
import hamenu from '../Assets/hamenu.png';

const Dashboard = () => {


    const queryParams = new URLSearchParams(window.location.search);
    const Username = queryParams.get('Username');

    const [Collapse, setCollapse] = useState(true);
    const [showImage, setShowImage] = useState(false);
    const [bold, setBold] = useState({ fontWeight: 'normal' });
    const [HBM, setHBM] = useState(false);
    const [Width, setWidth] = useState({
        
    });


    const [ImageStyle, setImageStyle] = useState({
        height: '25px',
        width: '25px',
        filter: 'invert(1)'
    });
    const [hambfunc, setHambfunc] = useState(false);

    const hamburgermenu = () => {
        setCollapse(prevState => !prevState);
        setHBM(prevState => !prevState);
        setWidth({
         width: '100vw'   
        })
    };

    const collp = () => {
        setCollapse(prevState => !prevState);
        setHBM(prevState => !prevState);
        
    };

    const handleShowO = () => {
        setShowImage(true);
        setBold({ fontWeight: 'bold' });
    };

    const handleShowL = () => {
        setShowImage(false);
        setBold({ fontWeight: 'normal' });
    };

    const imagehandleO = () => {
        setImageStyle({
            margin: '-2px',
            height: '28px',
            width: '28px',
            filter: 'invert(1)'
        });
    };

    const imagehandleL = () => {
        setImageStyle({
            height: '25px',
            width: '25px',
            filter: 'invert(1)'
        });
    };

    const ham = () => {
        setHambfunc(prevState => !prevState);
    };

    return (
        <div className='main'>
            {Collapse && (
                <div className="left">
                    <div className="lists account" onMouseEnter={handleShowO} onMouseLeave={handleShowL}>
                        <div className="noham">
                            <span style={bold}>@{Username}</span>
                            {showImage && (
                                <div>
                                    <img
                                        onClick={ham}
                                        onMouseEnter={imagehandleO}
                                        onMouseLeave={imagehandleL}
                                        className='imi'
                                        src={dwi}
                                        style={ImageStyle}
                                        alt="Profile Icon"
                                    />
                                </div>
                            )}
                        </div>
                        {hambfunc && (
                            <div className="ham">
                                <div className="hm1 logout">Log Out</div>
                                <div className="hm1 updateprofile">Update</div>
                            </div>
                        )}
                    </div>

                    <div className="pt lists dashboard">Dashboard</div>
                    <div className="pt lists reports">Reports</div>
                    <div className="pt lists settings">Settings</div>

                    <div className="footer">
                        <div className="pt letschat">
                            Let's Chat
                            <img className='helios' src={letschat} alt="Let's Chat Icon" />
                        </div>
                        <div className="pt collapse_menu" onClick={collp}>Collapse Menu</div>
                    </div>
                </div>
            )}

            <div className="right" style={solm}>
                {HBM && (<img
                    className='hamburger'
                    onClick={hamburgermenu}
                    src={hamenu}
                    alt="Hamburger Menu"
                />)}
            </div>
        </div>
    );
};

export default Dashboard;
