import React, { useState } from 'react';
import './Signup.css';
import username_icon from '../Assets/username.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const Signup = () => {
    const [user, setUser] = useState({
        Username: "",
        Email: "",
        Password: ""
    });

    const [passwordStrength, setPasswordStrength] = useState("");

    const handleInput = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value,
        });

        if (name === "Password") {
            const length = value.length;
            if (length > 10) {
                setPasswordStrength("Strong");
            } else if (length >= 6) {
                setPasswordStrength("Moderate");
            } else if (length > 0) {
                setPasswordStrength("Weak");
            } else {
                setPasswordStrength("");
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(user);
        try {
            const response = await fetch('http://localhost:5000/register', {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(user)
            });
            console.log(response);
            const data = await response.json(); // Await response.json()

            if (data.msg === "false1") {
                alert("Password must be at least 6 characters long, and include at least one letter, one number, and one special character.");
            } else if (data.msg === "false2") {
                alert("Please mention @ in email or add . after @");
            } else if (data.msg === "false3") {
                alert("Email already exists");
            } else if (data.newPost) {
                alert("Registration successful!");
                window.location.href = "http://localhost:3000/";
            }
        } catch (error) {
            alert("Details were not submitted try again later");
        }
    };

    return (
        <div className='container'>
            <div className="header">
                <div className="text">Signup</div>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="inputs">
                    <div className="input">
                        <img src={username_icon} alt="Username Icon" />
                        <input
                            type="text"
                            placeholder='Name'
                            name="Username"
                            value={user.Username}
                            onChange={handleInput}
                        />
                    </div>
                    <div className="input">
                        <img src={email_icon} alt="Email Icon" />
                        <input
                            type="email"
                            placeholder='Email'
                            name="Email"
                            value={user.Email}
                            onChange={handleInput}
                        />
                    </div>
                    <div className="input">
                        <img src={password_icon} alt="Password Icon" />
                        <input
                            type="password"
                            placeholder='Password'
                            name="Password"
                            value={user.Password}
                            onChange={handleInput}
                        />
                        {user.Password && (
                            <div className={`password-strength ${passwordStrength.toLowerCase()}`}>
                                {passwordStrength}
                            </div>
                        )}
                    </div>
                    
                    <div className="submit-container">
                        <div className="submit">
                            <button type="submit">Sign Up</button>
                        </div>
                        <div className="submit">
                            <button type="button"><a href="http://localhost:3000/">Login</a></button>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default Signup;
