import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import email_icon from '../Assets/email.png';
import "./ForgotP.css";
import backgroundVideo from '../Assets/background-video.mp4';

emailjs.init('LgH0VTdKwtSI3PsRw');

const ForgotP = () => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleInput = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Check if the email exists
      const response = await fetch('http://localhost:5000/check-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      const result = await response.json();

      if (result.exists) {
        // Fetch user data to get the password (or token) for resetting
        const userResponse = await fetch('http://localhost:5000/findvals', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        });

        const userData = await userResponse.json();
        const { Password } = userData;

        // Send email with password reset details
        await emailjs.send('service_jy6oq6w', 'template_m5k5p9e', {
          recipient: email,
          Password: Password,
        }, 'LgH0VTdKwtSI3PsRw');

        setMessage('Password has been sent to your email.');
      } else {
        setMessage("Email doesn't exist");
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred while processing your request.');
    } finally {
      setLoading(false);
      setEmail('');
    }
  };

  return (
    <div className='container'>
      {/* Background Video */}
      <video autoPlay muted loop className="background-video">
        <source src={backgroundVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className='content'>
        <div className="header">
          <div className="text">Password Reset</div>
          <div className="underline"></div>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="inputs">
            <div className="input">
              <img src={email_icon} alt="Email icon" />
              <input 
                type="email" 
                placeholder='Enter Your Email' 
                name="email" 
                value={email} 
                onChange={handleInput} 
                required
              />
            </div>
            <div className="submit-container">
              <div className="submit">
                <button type='submit' disabled={loading}>
                  {loading ? 'Submitting...' : 'Submit'}
                </button>
              </div>
            </div>
          </div>
          {message && <p className="message">{message}</p>}
        </form>
      </div>
    </div>
  );
};

export default ForgotP;
