import React, { useState } from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';
import letschat from '../Assets/letschat.png';
import hamenu from '../Assets/hamenu.png';
import dashboard from '../Assets/dashboard.png';
import reports from '../Assets/reports.png';
import settings from '../Assets/settings.png';

const Dashboard = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const Username = queryParams.get('Username');

  const [Collapse, setCollapse] = useState(true);
  const [showImage, setShowImage] = useState(false);
  const [bold, setBold] = useState({ fontWeight: 'normal' });
  const [HBM, setHBM] = useState(false);
  const [state, setState] = useState(false);
  const [uupdate, setUpdate] = useState(false);
  const [uval, setUval] = useState('');
  const [width, setWidth] = useState({ width: '80%' });
  const [dash, setDash] = useState(true);
  const [rep, setRep] = useState(false);
  const [set, setSet] = useState(false);
  const [letc, setLetc] = useState(false);

  const [message, setMessage] = useState('');
  const [ImageStyle, setImageStyle] = useState({
    height: '25px',
    width: '25px',
    filter: 'invert(1)',
  });
  const [hambfunc, setHambfunc] = useState(false);

  const dashStyle = {
   fontSize: '20px',
   fontWeight: "bolder",
   padding: "14px",
  //  marginLeft: '4px'
  background: "#ffffff",
  color: "black",
  boxShadow: '4px 3px 4px rgb(129, 132, 132)'
  };

  const repStyle = {
    marginTop: '12px',
    borderRadius: '12px',
    background: 'rgb(34, 39, 46)',
  };

  const setStyle = {
    marginTop: '12px',
    borderRadius: '12px',
    background: 'rgb(34, 39, 46)',
  };

  const letcStyle = {
    marginTop: '7px',
    background: 'rgb(34, 39, 46)',
    border: '1.592px solid white',
    padding: '10px',
    borderRadius: '10px',
    fontWeight: 'bolder',
  };

  const update = () => {
    setUpdate((prevState) => !prevState);
  };

  const logout = () => {
    document.cookie = 'token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
    window.location.replace('http://localhost:3000/');
  };

  const hamburgermenu = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState);
      return newState;
    });
  };

  const collp = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState);
      return newState;
    });
  };

  const sw = (newState) => {
    setWidth({
      width: newState ? '100%' : '80%',
    });
  };

  const onCD = () => {
    setDash(true);
    setLetc(false);
    setRep(false);
    setSet(false);
  };

  const onCR = () => {
    setDash(false);
    setLetc(false);
    setRep(true);
    setSet(false);
  };

  const onCL = () => {
    setDash(false);
    setLetc(true);
    setRep(false);
    setSet(false);
  };

  const onCS = () => {
    setDash(false);
    setLetc(false);
    setRep(false);
    setSet(true);
  };

  const handleShowO = () => {
    setShowImage(true);
    setBold({ fontWeight: 'bold' });
  };

  const handleShowL = () => {
    setShowImage(false);
    setBold({ fontWeight: 'normal' });
  };

  const imagehandleO = () => {
    setImageStyle({
      margin: '-2px',
      height: '28px',
      width: '28px',
      filter: 'invert(1)',
    });
  };

  const imagehandleL = () => {
    setImageStyle({
      height: '25px',
      width: '25px',
      filter: 'invert(1)',
    });
  };

  const ham = () => {
    setHambfunc((prevState) => !prevState);
  };

  const handleInput = (e) => {
    setUval(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/updatepass', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Username, uval }),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('Password updated successfully');
      } else {
        setMessage(result.msg || 'Error updating password');
      }
    } catch (error) {
      setMessage('There was an error');
    }
  };

  return (
    <div className="main">
      {Collapse && (
        <div className="left">
          <div className="topper">
            <div
              className="lists account"
              onMouseEnter={handleShowO}
              onMouseLeave={handleShowL}
            >
              <div className="noham">
                <span style={bold}>@{Username}</span>
                {showImage && (
                  <div>
                    <img
                      onClick={ham}
                      onMouseEnter={imagehandleO}
                      onMouseLeave={imagehandleL}
                      className="imi"
                      src={dwi}
                      style={ImageStyle}
                      alt="Profile Icon"
                    />
                  </div>
                )}
              </div>
              {hambfunc && (
                <div className="ham">
                  <div className="hm1 logout" onClick={logout}>
                    Log Out
                  </div>
                  <div className="hm1 updateprofile" onClick={update}>
                    Update
                  </div>
                </div>
              )}
            </div>

            <div
              className="pt lists dashboard"
              onClick={onCD}
              style={dash ? dashStyle : null}
            >
              <img src={dashboard} alt="dashboard's image" />
              Dashboard
            </div>
            <div
              className="pt lists reports"
              onClick={onCR}
              style={rep ? repStyle : null}
            >
              <img src={reports} alt="reports" />
              Reports
            </div>
            <div
              className="pt lists settings"
              onClick={onCS}
              style={set ? setStyle : null}
            >
              <img src={settings} alt="settings" />
              Settings
            </div>
          </div>
          <div className="footer">
            <div className="pt letschat" onClick={onCL} style={letc ? letcStyle : null}>
              Let's Chat . .
              <img className="helios" src={letschat} alt="Let's Chat Icon" />
            </div>
            <div className="pt collapse_menu" onClick={collp}>
              Collapse Menu
            </div>
          </div>
        </div>
      )}

      <div className="right" style={width}>
        {HBM && (
          <div className="hamim">
            <img
              className="hamburger"
              onClick={hamburgermenu}
              src={hamenu}
              alt="Hamburger Menu"
            />
          </div>
        )}
        {uupdate && (
          <div className="updatedb">
            <form onSubmit={handleSubmit}>
              <h1>Update Password</h1>
              <div className="text">
                <input
                  onChange={handleInput}
                  className="textbox2"
                  type="text"
                  name="update_password"
                  placeholder="Update password"
                />
              </div>
              <div className="upsub">
                <input className="button1" type="submit" value="Submit" />
              </div>
            </form>
            <p>{message}</p>
          </div>
        )}
        {!uupdate && dash && <h1>Dashboard</h1>}
        {!uupdate && rep && <h1>Reports</h1>}
        {!uupdate && letc && <h1>Let's Chat</h1>}
        {!uupdate && set && <h1>Settings</h1>}
      </div>
    </div>
  );
};

export default Dashboard;
