import React, { useState, useEffect } from 'react';
import './UpdateP.css';
import backgroundVideo from '../Assets/background-video.mp4';
import password_icon from '../Assets/password.png';

const UpdateP = () => {
    const queryParams = new URLSearchParams(window.location.search);
    const Email = queryParams.get('Email');
    const [uval, setUval] = useState('');
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');

    useEffect(() => {
        if (!Email) {
            setMessage('Invalid access. Email parameter is missing.');
        }
    }, [Email]);

    const handleInput = (e) => {
        setUval(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');

        if (!Email) {
            setMessage('Cannot update password. Email is missing.');
            setLoading(false);
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/findUsername', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ Email }),
            });

            const user = await response.json();
            if (user.msg === 'failed') {
                setMessage('Failed to fetch username.');
                setLoading(false);
                return;
            }

            const Username = user.found.Username;
            const updateResponse = await fetch('http://localhost:5000/updatepass', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ Username, uval }),
            });

            const result = await updateResponse.json();
            if (updateResponse.ok) {
                setMessage('Password updated successfully');
            } else {
                setMessage(result.msg || 'Error updating password');
            }
        } catch (error) {
            setMessage('There was an error');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='container'>
            <video autoPlay muted loop className="background-video">
                <source src={backgroundVideo} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div className="content">
                <div className="header">
                    <div className="text">Update Password</div>
                    <div className="underline"></div>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="inputs">
                        <div className="input">
                            <img src={password_icon} alt="Password Icon" />
                            <input
                                type="password"
                                placeholder='New Password'
                                name="Password"
                                onChange={handleInput}
                                required
                                autoComplete="new-password"
                            />
                        </div>
                        <div className="submit-container">
                            <div className="submit">
                                <button type='submit' disabled={loading}>
                                    {loading ? 'Updating...' : 'Update'}
                                </button>
                            </div>
                        </div>
                    </div>
                </form>
                {message && <p>{message}</p>}
            </div>
        </div>
    );
};

export default UpdateP;
