import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import email_icon from '../Assets/email.png';
import "./ForgotP.css"
import backgroundVideo from '../Assets/background-video.mp4';

emailjs.init('LgH0VTdKwtSI3PsRw');
const ForgotP = () => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [value, setValue] = useState('');
  const handleInput = (e) => {
    setEmail(e.target.value);
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');


    try {
      // Check if the email exists
      const response = await fetch('http://localhost:5000/check-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }), // Send email as JSON
      });

      const result 
      = await response.json();

      if(result.exists){
        await emailjs.send( 'service_jy6oq6w','template_m5k5p9e' , {
           recipient: email,
         }, 'LgH0VTdKwtSI3PsRw');
      } else {
        alert("Email doesn't exist");
      }
      
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred while checking the email.');
    } finally {
      setLoading(false);
      setEmail('');
    }
  };

  return (
    <div className='container'>
            {/* Background Video */}
            <video autoPlay muted loop className="background-video">
                <source src={backgroundVideo} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
    <div className='content'>
      <div className="header">
        <div className="text">Password Reset</div>
        <div className="underline"></div>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="inputs">
          <div className="input">
            <img src={email_icon} alt="Email icon" />
            <input 
              type="email" 
              placeholder='Enter Your Email' 
              name="email" 
              value={email} 
              onChange={handleInput} 
              required
            />
          </div>
          <div className="submit-container">
            <div className="submit">
              <button type='submit' disabled={loading}>
                {loading ? 'Submitting...' : 'Submit'}
              </button>
            </div>
          </div>
        </div>
      </form>
    </div>
    </div>
  );
};

export default ForgotP;
