import emailjs from 'emailjs-com';
import email_icon from '../Assets/email.png';
import React, { useState } from 'react';

const ForgotP = async () => {
  const [email, setEmail] = useState('');

  const handleInput = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/check-email', {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ email }) 
      });

      const result = await response.json(); 

      if (result.msg === "true") {
      
        emailjs.sendForm('service_jy6oq6w', 'template_m5k5p9e', e.target, 'adityaadangwal@gmail.com')
          .then((result) => {
            console.log(result.text);
            alert('Password reset email sent!');
          }, (error) => {
            console.log(error.text);
            alert('Failed to send password reset email.');
          });

      } else {
        alert("Email doesn't exist");
      }

    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while checking the email.');
    } finally {
      setEmail('');
    }
  };

  return (
    <div className='container'>
      <div className="header">
        <div className="text">Password Reset</div>
        <div className="underline"></div>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="inputs">
          <div className="input">
            <img src={email_icon} alt="Email icon" />
            <input 
              type="email" 
              placeholder='Enter Your Email' 
              name="email" 
              value={email} 
              onChange={handleInput} 
            />
          </div>
          <div className="submit-container">
            <div className="submit"><button type='submit'>Submit</button></div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ForgotP;
