import React, { useState } from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';
import letschat from '../Assets/letschat.png';
import hamenu from '../Assets/hamenu.png';
import dashboard from '../Assets/dashboard.png'
import reports from '../Assets/reports.png'
import settings from '../Assets/'
const Dashboard = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const Username = queryParams.get('Username');

  const [Collapse, setCollapse] = useState(true);
  const [showImage, setShowImage] = useState(false);
  const [bold, setBold] = useState({ fontWeight: 'normal' });
  const [HBM, setHBM] = useState(false);
  const [state, setState] = useState(false);
  const [uupdate, setUpdate] = useState(false);
  const [uval, setUval] = useState('');
  const [width, setWidth] = useState({
    width: "80%",
  });
  const [message, setMessage] = useState('');

  const update = () => {
    setUpdate((prevState) => !prevState); // Show the update form
  };

  const logout = () => {
    
    document.cookie = "token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
    window.location.replace("http://localhost:3000/");
  };
  

  const [ImageStyle, setImageStyle] = useState({
    height: '25px',
    width: '25px',
    filter: 'invert(1)',
  });
  const [hambfunc, setHambfunc] = useState(false);

  const hamburgermenu = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState); 
      return newState; 
    });
  };

  const collp = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState);
      return newState;
    });
  };

  const sw = (newState) => {
    setWidth({
      width: newState ? "100%" : "80%", 
    });
  };

  const handleShowO = () => {
    setShowImage(true);
    setBold({ fontWeight: 'bold' });
  };

  const handleShowL = () => {
    setShowImage(false);
    setBold({ fontWeight: 'normal' });
  };

  const imagehandleO = () => {
    setImageStyle({
      margin: '-2px',
      height: '28px',
      width: '28px',
      filter: 'invert(1)',
    });
  };

  const imagehandleL = () => {
    setImageStyle({
      height: '25px',
      width: '25px',
      filter: 'invert(1)',
    });
  };

  const ham = () => {
    setHambfunc((prevState) => !prevState);
  };

  const handleInput = (e) => {
    setUval(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/updatepass', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Username, uval }),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage("Password updated successfully");
      } else {
        setMessage(result.msg || "Error updating password");
      }
    } catch (error) {
      setMessage("There was an error");
    }
  };

  return (
    <div className='main'>
      {Collapse && (
        <div className="left">
          <div className="topper">
          <div className="lists account" onMouseEnter={handleShowO} onMouseLeave={handleShowL}>
            <div className="noham">
              <span style={bold}>@{Username}</span>
              {showImage && (
                <div>
                  <img
                    onClick={ham}
                    onMouseEnter={imagehandleO}
                    onMouseLeave={imagehandleL}
                    className='imi'
                    src={dwi}
                    style={ImageStyle}
                    alt="Profile Icon"
                  />
                </div>
              )}
            </div>
            {hambfunc && (
              <div className="ham">
                <div className="hm1 logout" onClick={logout}>Log Out</div>
                <div className="hm1 updateprofile" onClick={update}>Update</div>
              </div>
            )}
          </div>

          <div className="pt lists dashboard"><img src={dashboard} alt="dashboard's image" />Dashboard</div>
          <div className="pt lists reports"><img src={reports} alt="reports"/>Reports</div>
          <div className="pt lists settings">Settings</div>
          </div>
          <div className="footer">
            <div className="pt letschat">
              Let's Chat . .
              <img className='helios' src={letschat} alt="Let's Chat Icon" />
            </div>
            <div className="pt collapse_menu" onClick={collp}>Collapse Menu</div>
          </div>
        </div>
      )}

      <div className="right" style={width}>
        {HBM && (
          <div className="hamim">
            <img
              className='hamburger'
              onClick={hamburgermenu}
              src={hamenu}
              alt="Hamburger Menu"
            />
          </div>
        )}
        {uupdate && (
          <div className="updatedb">
            <form onSubmit={handleSubmit}>
              <h1>Update Password</h1>
              <div className="text">
                <input 
                  onChange={handleInput} 
                  className="textbox2" 
                  type="text" 
                  name='update_password' 
                  placeholder='Update password' 
                />
              </div>
              <div className="upsub">
                <button className='subbutone' type="submit">Submit</button>
              </div>
              {message && <p className="message">{message}</p>}
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
