import React, { useState } from 'react';
import './Login_signup.css';
import username_icon from '../Assets/username.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const Login_signup = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLogin, setIsLogin] = useState(true);
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (isLogin) {
            try {
                await axios.post('http://localhost:5000/login', { Username: username, Password: password });
                history.push('/profile');
            } catch (error) {
                console.error(error);
            }
        } else {
            try {
                await axios.post('http://localhost:5000/register', { Username: username, Email: email, Password: password });
                setIsLogin(true);
            } catch (error) {
                console.error(error);
            }
        }
    };

    return (
        <div className='container'>
            <div className="header">
                <div className="text">{isLogin ? 'Login' : 'Sign Up'}</div>
                <div className="underline"></div>
            </div>
            <form className="inputs" onSubmit={handleSubmit}>
                {!isLogin && (
                    <div className="input">
                        <img src={username_icon} alt="Username" />
                        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder='Name' required />
                    </div>
                )}
                <div className="input">
                    <img src={email_icon} alt="Email" />
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder='Email' required />
                </div>
                <div className="input">
                    <img src={password_icon} alt="Password" />
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder='Password' required />
                </div>
                <div className="forgot-password">
                    {isLogin ? 'Lost Password ' : 'Already have an account? '}<span onClick={() => setIsLogin(!isLogin)}>{isLogin ? 'Click here' : 'Login'}</span>
                </div>
                <div className="submit-container">
                    <button type="submit" className="submit">{isLogin ? 'Login' : 'Sign Up'}</button>
                </div>
            </form>
        </div>
    );
};

export default Login_signup;
