import React, { useState } from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';
import letschat from '../Assets/letschat.png';
import hamenu from '../Assets/hamenu.png';
import dashboard from '../Assets/dashboard.png';
import reports from '../Assets/reports.png';
import settings from '../Assets/settings.png';
import attach from '../Assets/attach.png';
import dialog from '../Assets/dialog.png';
import Button from '@mui/material/Button';
import { motion } from 'framer-motion';
import send_icon from '../Assets/send_icon.png';
import { Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';

const Dashboard = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const Username = queryParams.get('Username');

  const [Tdialog, setTdialog] = useState(false);
  const [Collapse, setCollapse] = useState(false);
  const [showImage, setShowImage] = useState(false);
  const [bold, setBold] = useState({ fontWeight: 'normal' });
  const [HBM, setHBM] = useState(false);
  const [state, setState] = useState(false);
  const [uupdate, setUpdate] = useState(false);
  const [uval, setUval] = useState('');
  const [dash, setDash] = useState(true);
  const [rep, setRep] = useState(false);
  const [set, setSet] = useState(false);
  const [letc, setLetc] = useState(false);
  const [prompt, setPrompt] = useState('');
  const [message, setMessage] = useState('');
  const [dialogOpen, setDialogOpen] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [ImageStyle, setImageStyle] = useState({
    height: '25px',
    width: '25px',
    filter: 'invert(1)',
  });

  const handlePrompt = (e) => {
    setPrompt(e.target.value);
  };

  const handlePromsub = () => {
    // Define logic for handlePromsub if needed
  };

  const [hambfunc, setHambfunc] = useState(false);

  const dashStyle = {
    fontSize: '20px',
    fontWeight: "bolder",
    padding: "14px",
    background: "black",
    boxShadow: '4px 3px 4px rgb(129, 132, 132)'
  };

  const repStyle = {
    fontSize: '20px',
    fontWeight: "bolder",
    padding: "14px",
    background: "black",
    boxShadow: '4px 3px 4px rgb(129, 132, 132)'
  };

  const setStyle = {
    fontSize: '20px',
    fontWeight: "bolder",
    padding: "14px",
    background: "black",
    boxShadow: '4px 3px 4px rgb(129, 132, 132)'
  };

  const letcStyle = {
    fontSize: '20px',
    fontWeight: "bolder",
    padding: "14px",
    background: "black",
    boxShadow: '4px 3px 4px rgb(129, 132, 132)'
  };

  const update = () => {
    setUpdate((prevState) => !prevState);
    setUval('');
    setMessage('');
  };

  const handleReset = () => {
    setUval('');
    setMessage('');
  };

  const logout = () => {
    document.cookie = 'token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
    window.location.replace('http://localhost:3000/');
  };

  const hamburgermenu = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState);
      return newState;
    });
  };

  const collp = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
  };

  const sw = (newState) => {
    // Set width logic commented out
    // setWidth({
    //   width: newState ? '100%' : '80%',
    // });
  };

  const onCD = () => {
    setDash(true);
    setLetc(false);
    setRep(false);
    setSet(false);
  };

  const onCR = () => {
    setDash(false);
    setLetc(false);
    setRep(true);
    setSet(false);
  };

  const onCL = () => {
    setDash(false);
    setLetc(true);
    setRep(false);
    setSet(false);
  };

  const onDialogMouse = () => {
    setTdialog((prevState) => !prevState);
  };

  const onCS = () => {
    setDash(false);
    setLetc(false);
    setRep(false);
    setSet(true);
  };

  const handleShowO = () => {
    setShowImage(true);
    setBold({ fontWeight: 'bold' });
  };

  const handleShowL = () => {
    setShowImage(false);
    setBold({ fontWeight: 'normal' });
  };

  const imagehandleO = () => {
    setImageStyle({
      margin: '-2px',
      height: '28px',
      width: '28px',
      filter: 'invert(1)',
    });
  };

  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files); // Store selected files in state
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
  };

  const handleDialogOpen = () => {
    setDialogOpen(true);
  };

  const imagehandleL = () => {
    setImageStyle({
      height: '25px',
      width: '25px',
      filter: 'invert(1)',
    });
  };

  const ham = () => {
    setHambfunc((prevState) => !prevState);
  };

  const handleFileSubmit = (e) => {
    e.preventDefault();
    // Define logic for file submit if needed
  };

  const handleInput = (e) => {
    setUval(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (uval.length === 0) {
      return setMessage('Password field is required');
    }

    try {
      const response = await fetch('http://localhost:5000/updatepass', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Username, uval }),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('Password updated successfully');
      } else {
        setMessage(result.msg || 'Error updating password');
      }
    } catch (error) {
      setMessage('There was an error');
    }
  };

  return (
    <div className="main">
      {Collapse && (
        <motion.div
          className="left"
          initial={{ width: '16%' }}
          animate={{ width: Collapse ? '16%' : '0%' }}
          transition={{ duration: 0.5 }}
        >
          <div className="topper">
            <div
              className="lists account"
              onMouseEnter={handleShowO}
              onMouseLeave={handleShowL}
            >
              <div className="noham">
                <span style={bold}>@{Username}</span>
                {showImage && (
                  <div>
                    <img
                      onClick={ham}
                      onMouseEnter={imagehandleO}
                      onMouseLeave={imagehandleL}
                      className="imi"
                      src={dwi}
                      style={ImageStyle}
                      alt="Profile Icon"
                    />
                  </div>
                )}
              </div>
              {hambfunc && (
                <div className="ham">
                  <div className="hm1 logout" onClick={logout}>
                    Log Out
                  </div>
                  <div className="hm1 updateprofile" onClick={update}>
                    Update
                  </div>
                </div>
              )}
            </div>

            <div
              className="pt lists dashboard"
              onClick={onCD}
              style={dash ? dashStyle : null}
            >
              <img src={dashboard} alt="dashboard's image" />
              Dashboard
            </div>
            <div
              className="pt lists reports"
              onClick={onCR}
              style={rep ? repStyle : null}
            >
              <img src={reports} alt="reports" />
              Reports
            </div>
            <div
              className="pt lists settings"
              onClick={onCS}
              style={set ? setStyle : null}
            >
              <img src={settings} alt="settings" />
              Settings
            </div>
          </div>
          <div className="footer">
            <div
              className="pt letschat"
              onClick={onCL}
              style={letc ? letcStyle : null}
            >
              Let's Chat . .
              <img className="lets" src={letschat} alt="letschat" />
            </div>
            <div className="pt attach" onClick={onDialogMouse}>
              <img className="attach" src={attach} alt="attach" />
              Attach
            </div>
          </div>
        </motion.div>
      )}

      <motion.div
        className="right"
        initial={{ width: '84%' }}
        animate={{ width: Collapse ? '84%' : '100%' }}
        transition={{ duration: 0.5 }}
      >
        <div className="top">
          <img
            src={hamenu}
            alt="hamburger menu"
            onClick={hamburgermenu}
            className="hamburger"
          />
          <div className="top_title">Dashboard</div>
        </div>

        <div className="content">
          {dash && <div className="contentTitle">Dashboard Content</div>}
          {rep && <div className="contentTitle">Reports Content</div>}
          {set && (
            <div className="contentTitle">
              Settings Content
              {uupdate && (
                <div className="updatePopup">
                  <Dialog open={dialogOpen} onClose={handleDialogClose}>
                    <DialogTitle>Update Password</DialogTitle>
                    <DialogContent>
                      <form onSubmit={handleSubmit}>
                        <input
                          type="password"
                          value={uval}
                          onChange={handleInput}
                          placeholder="Enter new password"
                        />
                        <div>{message}</div>
                        <Button type="submit">Update</Button>
                      </form>
                    </DialogContent>
                    <DialogActions>
                      <Button onClick={handleDialogClose}>Cancel</Button>
                      <Button onClick={handleReset}>Reset</Button>
                    </DialogActions>
                  </Dialog>
                </div>
              )}
            </div>
          )}
          {letc && <div className="contentTitle">Let's Chat Content</div>}
        </div>

        <Dialog open={Tdialog} onClose={onDialogMouse}>
          <DialogTitle>Dialog Title</DialogTitle>
          <DialogContent>
            <textarea
              value={prompt}
              onChange={handlePrompt}
              placeholder="Type your prompt here"
            />
            <Button onClick={handlePromsub} variant="contained">
              Submit
            </Button>
            <div className="message">{message}</div>
          </DialogContent>
          <DialogActions>
            <Button onClick={onDialogMouse}>Close</Button>
          </DialogActions>
        </Dialog>
      </motion.div>
    </div>
  );
};

export default Dashboard;
