import React, {useState} from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';
const  Dashboard = ()=>{
    const queryParams = new URLSearchParams(window.location.search);
    const Username = queryParams.get('Username');
    const [showImage, setShowImage] = useState(false);
    const [bold, setBold] = useState({
        fontWeight: 'normal'
    })
    const setbolder = ()=>{
        setBold({
        fontWeight: 'bold'
        })
    }
    const handleShowO = ()=>{
        setShowImage(true);
        setBold({ fontWeight: 'bold' });
    };
    const handleShowL = ()=>{
        setShowImage(false);
        setBold({ fontWeight: 'normal' });
    };
    const [imageStyle, setImageStyle] = useState({
        height: '25px',
        width: '25px',
        filter: 'invert(1)'
    });

    // const handleMouseOver = () =>{
    //     setImageStyle({
    //         height: '28px',
    //     width: '28px',
    //     });
    // }
    // const handleMouseOver = ()=>{
    //     setImageStyle({
    //         height: '25px',
    //         width: '25px'
    //     });
    // }

    
    return (

        <div className='main'>
              <div className="left">
               <div  onMouseEnter={handleShowO} onMouseLeave={handleShowL} className="lists account">
                <span on >@{Username}</span>
                {showImage && (
                    <div>

                        <img class='imi' src={dwi} style={imageStyle} alt="image" />
                    </div>
                    )}
               </div>
               <div className="lists dashboard">
               </div>
               <div className="lists reports">
               </div>
               <div className="lists settings">
               </div>
               <div className="letschat">
               </div>
               <div className="collapse_menu">
               </div>
              </div>
              <div className="right">

              </div>
        </div>
    );
}
export default Dashboard
