import React, { useState } from 'react';
import './Dashboard.css';
import dwi from '../Assets/dw.png';
import letschat from '../Assets/letschat.png';
import hamenu from '../Assets/hamenu.png';
import dashboard from '../Assets/dashboard.png';
import reports from '../Assets/reports.png';
import settings from '../Assets/settings.png';
import attach from '../Assets/attach.png';
import dialog from '../Assets/dialog.png';
import Button from '@mui/material/Button';
import send_icon from '../Assets/send_icon.png';
import { Dialog, DialogTitle, DialogContent, DialogActions, TextField } from '@mui/material';

const Dashboard = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const Username = queryParams.get('Username');
  const [Tdialog, setTdialog] = useState(false);
  const [Collapse, setCollapse] = useState(true);
  const [showImage, setShowImage] = useState(false);
  const [bold, setBold] = useState({ fontWeight: 'normal' });
  const [HBM, setHBM] = useState(false);
  const [state, setState] = useState(false);
  const [uupdate, setUpdate] = useState(false);
  const [uval, setUval] = useState('');
  const [width, setWidth] = useState({ width: '80%' });
  const [dash, setDash] = useState(true);
  const [rep, setRep] = useState(false);
  const [set, setSet] = useState(false);
  const [letc, setLetc] = useState(false);
  const [prompt, setPrompt] = useState('');
  const [message, setMessage] = useState('');
  const [ImageStyle, setImageStyle] = useState({
    height: '25px',
    width: '25px',
    filter: 'invert(1)',
  });
  const [hambfunc, setHambfunc] = useState(false);
  const [dialogOpen, setDialogOpen] = useState(false); // State to control dialog visibility

  const handlePrompt = (e) => {
    setPrompt(e.target.value);
  };

  const handlePromsub = (e) => {
    e.preventDefault();
    // Add your form submission logic here
  };

  const dashStyle = {
    fontSize: '20px',
    fontWeight: 'bolder',
    padding: '14px',
    background: 'black',
    boxShadow: '4px 3px 4px rgb(129, 132, 132)',
  };

  // Handle opening and closing of dialog
  const handleDialogOpen = () => {
    setDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
  };

  const update = () => {
    setUpdate((prevState) => !prevState);
    setUval('');
    setMessage('');
    handleDialogOpen(); // Open dialog when update is clicked
  };

  const handleReset = () => {
    setUval('');
    setMessage('');
  };

  const logout = () => {
    document.cookie = 'token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
    window.location.replace('http://localhost:3000/');
  };

  const hamburgermenu = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState);
      return newState;
    });
  };

  const collp = () => {
    setCollapse((prevState) => !prevState);
    setHBM((prevState) => !prevState);
    setState((prevState) => {
      const newState = !prevState;
      sw(newState);
      return newState;
    });
  };

  const sw = (newState) => {
    setWidth({
      width: newState ? '100%' : '80%',
    });
  };

  const onCD = () => {
    setDash(true);
    setLetc(false);
    setRep(false);
    setSet(false);
  };

  const onCR = () => {
    setDash(false);
    setLetc(false);
    setRep(true);
    setSet(false);
  };

  const onCL = () => {
    setDash(false);
    setLetc(true);
    setRep(false);
    setSet(false);
  };

  const onDialogMouse = () => {
    setTdialog((prevState) => !prevState);
  };

  const onCS = () => {
    setDash(false);
    setLetc(false);
    setRep(false);
    setSet(true);
  };

  const handleShowO = () => {
    setShowImage(true);
    setBold({ fontWeight: 'bold' });
  };

  const handleShowL = () => {
    setShowImage(false);
    setBold({ fontWeight: 'normal' });
  };

  const imagehandleO = () => {
    setImageStyle({
      margin: '-2px',
      height: '28px',
      width: '28px',
      filter: 'invert(1)',
    });
  };

  const imagehandleL = () => {
    setImageStyle({
      height: '25px',
      width: '25px',
      filter: 'invert(1)',
    });
  };

  const ham = () => {
    setHambfunc((prevState) => !prevState);
  };

  const handleInput = (e) => {
    setUval(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (uval.length === 0) {
      return setMessage('Password field is required');
    }

    try {
      const response = await fetch('http://localhost:5000/updatepass', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Username, uval }),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage('Password updated successfully');
        handleDialogClose(); // Close dialog on successful update
      } else {
        setMessage(result.msg || 'Error updating password');
      }
    } catch (error) {
      setMessage('There was an error');
    }
  };

  return (
    <div className="main">
      {Collapse && (
        <div className="left">
          {/* Rest of your left panel */}
          <div className="hm1 updateprofile" onClick={update}>
            Update
          </div>
        </div>
      )}

      {/* Right panel and other sections */}
      
      {/* Dialog for updating profile */}
      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle>Update Profile</DialogTitle>
        <DialogContent>
          <form onSubmit={handleSubmit}>
            <TextField
              onChange={handleInput}
              type="text"
              name="update_password"
              label="Update Password"
              fullWidth
              margin="dense"
            />
            <div className="mess">{message}</div>
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default Dashboard;
